import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { CardActionArea, CardActions, CardContent, CardMedia,Button, Typography, IconButton, Card} from '@material-ui/core/';


const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    backgroundColor: "#EFEBE9",
  },
  media: {
    height: 140,
  },
});

export default function MediaCard( {Name, Description, img, imgAlt }) {
  const classes = useStyles();

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={img}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {Name}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {Description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary">
          Share
        </Button>
        <IconButton size="small" color="primary" >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
    </Card>
  );
}
// href="https://www.myrecipes.com/recipe/pepperoni-pizza"
