import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { CardActionArea, CardActions, CardContent, CardMedia,Button, Typography, IconButton, Card, Collapse} from '@material-ui/core/';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import List from './List'
import items from './items';

const useStyles = makeStyles({
  root: {
    maxWidth: 345,
    backgroundColor: "#EFEBE9",
  },
  media: {
    height: 140,
  },
});



export default function MediaCard( {Name, Description, img,}) {
  const classes = useStyles();
  const [expanded, setExpanded] = React.useState(false);

  const handleExpandClick = () => {
    setExpanded(!expanded);
  };

  return (
    <Card className={classes.root}>
      <CardActionArea>
        <CardMedia
          className={classes.media}
          image={img}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="h2">
            {Name}
          </Typography>
          <Typography variant="body2" color="textSecondary" component="p">
            {Description}
          </Typography>
        </CardContent>
      </CardActionArea>
      <CardActions>
        <Button size="small" color="primary" target="_blank"href="https://www.myrecipes.com/recipe/pepperoni-pizza">
          Share
        </Button>
        <IconButton size="small" color="primary" 
          className={classes.expanded}
          onClick={handleExpandClick}
        >
          <ExpandMoreIcon />
        </IconButton>
      </CardActions>
      <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Ingredients:</Typography>
          <Typography paragraph>
            <List Ingredients={items[0].Ingredients}/>
          </Typography>
        </CardContent>
      </Collapse>
    </Card>
  );
}
// href="https://www.myrecipes.com/recipe/pepperoni-pizza"
