import React, {useState} from 'react';
import MenuIcon from '@material-ui/icons/Menu';
import { AppBar, Toolbar, Button, IconButton, Typography, Box, InputBase } from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: "#00CED1",
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
    searchBox: {
      paddingLeft: '0',
      marginLeft: 'auto',
      background: '#fff',
      borderRadius:'7px',
      boxShadow: '5px',
      paddingTop: 0,
      paddingBottom: 0,
    },
    SearchField: {
      paddingRight: '100px'
    }
  }));



const Header = () => {
  const [search, setSearch] = useState("");
  const handleSearchChange = (event) => {
    setSearch=(event.target.value);
  }; 

    const classes = useStyles();
    return(
        <div>
            <AppBar className={classes.root} position='static'>
                <Toolbar>
                    <IconButton edge='start'>
                        <MenuIcon/>
                    </IconButton>
                    <Box ml={3} mr={4}>
                        <Typography variant='h6'> MyCookBook </Typography>
                    </Box>
                    <Typography className={classes.searchBox} edge='end'>
                        <IconButton>
                            <SearchIcon/>
                        </IconButton>
                        <InputBase 
                        className={classes.SearchField} 
                        placeholder='Search...'
                        value={search}
                        onChange={console.log(handleSearchChange)}
                        />
                    </Typography>
                </Toolbar>
            </AppBar>
        </div>
    )
}

export default Header;
