import React from 'react';
import MenuIcon from '@material-ui/icons/Menu';
import AccountCircleIcon from '@material-ui/icons/AccountCircle';
import { AppBar, Toolbar, IconButton, Typography, Box, InputBase } from '@material-ui/core';
import {makeStyles} from '@material-ui/core/styles';
import SearchIcon from '@material-ui/icons/Search';

const useStyles = makeStyles((theme) => ({
    root: {
        backgroundColor: "#00CED1",
        flexGrow: 1,
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    title: {
        flexGrow: 1,
    },
    searchBox: {
      paddingLeft: '0',
      marginLeft: 'auto',
      background: '#fff',
      borderRadius:'700px',
      boxShadow: '5px',
      paddingTop: 0,
      paddingBottom: 0,
    },
    SearchField: {
      paddingRight: '100px'
    },
    accountCircle:{
      marginRight:'auto',
    }
  }));



function Header({handleSearchChange}) {



    const classes = useStyles();
    return(
        <div>
            <AppBar className={classes.root} position='static'>
                <Toolbar>
                    <IconButton edge='start'>
                        <MenuIcon/>
                    </IconButton>
                    <Box ml={3} mr={4}>
                        <Typography variant='h6'> MyCookBook </Typography>
                    </Box>
                    <Typography className={classes.searchBox} edge='end'>
                        <IconButton>
                            <SearchIcon/>
                        </IconButton>
                        <InputBase 
                        className={classes.SearchField} 
                        placeholder='Search...'

                        onChange={handleSearchChange}
                        />
                    </Typography>
                    <IconButton>
                      <AccountCircleIcon className={classes.accountCircle} fontSize={"large"}/>
                    </IconButton>
                </Toolbar>
            </AppBar>
        </div>
    )
}

export default Header;
